# Solução simples
pilha = []
while True:
    s = input().strip()
    if s == 'final feliz':
        break
    if s == 'lavando':
        if pilha:
            nome = pilha.pop()
            print('lavando prato de', nome)
        else:
            pass
    else:
        pilha.append(s)
